#define ADC_CR1_REG_OFFSET      0x04UL
#define ADC_CR1_REG_ADDR        (ADC_BASE_ADDR + ADC_CR1_REG_OFFSET)

#define RCC_BASE_ADDR           0x40023800UL // UL-> Unsigned long because address is always unsigned
#define RCC_APB2_REG_OFFSET     0x44UL
#define RCC_ENR_APB2_REG_ADDR   (RCC_BASE_ADDR + RCC_APB2_REG_OFFSET)

