  uint32_t *pADCCRCCAPB2Reg = (uint32_t*) RCC_ENR_APB2_REG_ADDR;
  
  *pADCCRCCAPB2Reg |= (1 << 8);
  *pADCCR1Reg |= (1 << 8);
  
  /* Loop forever */
	for(;;);
