#include <stdint.h>
#include "main.h"

#define STEPS_PER_SEC 200000

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

extern void initialise_monitor_handles(void);

static void sleep(int secs);

static void initialise_peripherals(RCC_AHB1ENR_t volatile *const pRCC_AHB1ENR, 
GPIOx_MODE_t  volatile  *const pGPIOE_MODE, 
GPIOx_MODE_t  volatile  *const pGPIOD_MODE,
GPIOx_PUPDR_t volatile  *const pGPIOD_PUPDR);

static void toggleLED(GPIOx_ODR_t volatile *const pGPIOE_ODR);

int main(void)
{
  initialise_monitor_handles();  
  RCC_AHB1ENR_t volatile *const pRCC_AHB1ENR = (RCC_AHB1ENR_t*)   0x40023830;
  
  GPIOx_PUPDR_t volatile *const pGPIOD_PUPDR = (GPIOx_PUPDR_t*)   0x40020C0C;
  
  GPIOx_MODE_t  volatile *const pGPIOE_MODE  = (GPIOx_MODE_t*)    0x40021000;
  GPIOx_MODE_t  volatile *const pGPIOD_MODE  = (GPIOx_MODE_t*)    0x40020C00;

  GPIOx_ODR_t   volatile *const pGPIOE_ODR   = (GPIOx_ODR_t*)     0x40021014;
  GPIOx_ODR_t   volatile *const pGPIOD_ODR   = (GPIOx_ODR_t*)     0x40020C14;
  
  GPIOx_IDR_t   volatile *const pGPIOD_IDR   = (GPIOx_IDR_t*)     0x40020C10;

  initialise_peripherals(pRCC_AHB1ENR, pGPIOE_MODE, pGPIOD_MODE, pGPIOD_PUPDR);  

  /* Loop forever */
  while (1) {
    // [1:A]
    pGPIOD_ODR->ODR0 = 0;
    pGPIOD_ODR->ODR1 = 1;
    pGPIOD_ODR->ODR2 = 1;
    pGPIOD_ODR->ODR3 = 1;

    if (!pGPIOD_IDR->IDR8) {
      toggleLED(pGPIOE_ODR);
      printf("Key 1 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR9) {
      toggleLED(pGPIOE_ODR);
      printf("Key 2 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR10) {
      toggleLED(pGPIOE_ODR);
      printf("Key 3 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR11) {
      toggleLED(pGPIOE_ODR);
      printf("Key A Pressed\n");
    }

    // [4:B]
    pGPIOD_ODR->ODR0 = 1;
    pGPIOD_ODR->ODR1 = 0;
    pGPIOD_ODR->ODR2 = 1;
    pGPIOD_ODR->ODR3 = 1;

    if (!pGPIOD_IDR->IDR8) {
      toggleLED(pGPIOE_ODR);
      printf("Key 4 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR9) {
      toggleLED(pGPIOE_ODR);
      printf("Key 5 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR10) {
      toggleLED(pGPIOE_ODR);
      printf("Key 6 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR11) {
      toggleLED(pGPIOE_ODR);
      printf("Key B Pressed\n");
    }

    // [7:C]
    pGPIOD_ODR->ODR0 = 1;
    pGPIOD_ODR->ODR1 = 1;
    pGPIOD_ODR->ODR2 = 0;
    pGPIOD_ODR->ODR3 = 1;

    if (!pGPIOD_IDR->IDR8) {
      toggleLED(pGPIOE_ODR);
      printf("Key 7 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR9) {
      toggleLED(pGPIOE_ODR);
      printf("Key 8 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR10) {
      toggleLED(pGPIOE_ODR);
      printf("Key 9 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR11) {
      toggleLED(pGPIOE_ODR);
      printf("Key C Pressed\n");
    }

    // [*:D]
    pGPIOD_ODR->ODR0 = 1;
    pGPIOD_ODR->ODR1 = 1;
    pGPIOD_ODR->ODR2 = 1;
    pGPIOD_ODR->ODR3 = 0;

    if (!pGPIOD_IDR->IDR8) {
      toggleLED(pGPIOE_ODR);
      printf("Key * Pressed\n");
    }
    if (!pGPIOD_IDR->IDR9) {
      toggleLED(pGPIOE_ODR);
      printf("Key 0 Pressed\n");
    }
    if (!pGPIOD_IDR->IDR10) {
      toggleLED(pGPIOE_ODR);
      printf("Key # Pressed\n");
    }
    if (!pGPIOD_IDR->IDR11) {
      toggleLED(pGPIOE_ODR);
      printf("Key D Pressed\n");
    }
  }
}

static void sleep(int secs) {
  unsigned int i,s;
  for (s=0; s < secs; s++) {
    for (i=0; i < STEPS_PER_SEC; i++) {
       // skip CPU cycle or any other statement(s) for making loop
       // untouched by C compiler code optimizations
       asm("nop");
    }
  }
}

static void initialise_peripherals(RCC_AHB1ENR_t volatile *const pRCC_AHB1ENR, 
GPIOx_MODE_t  volatile  *const pGPIOE_MODE, 
GPIOx_MODE_t  volatile  *const pGPIOD_MODE,
GPIOx_PUPDR_t volatile  *const pGPIOD_PUPDR
) {
  // Enabling RCC AHB1 clock for GPIOE and GPIOD
  pRCC_AHB1ENR->GPIOE_EN = 1;
  pRCC_AHB1ENR->GPIOD_EN = 1;

  // Setting PE4 as output (LED)
  pGPIOE_MODE->MODER4 = 0x01;

  // Setting GPIOs PD0-PD3 mode as output (Rows)
  pGPIOD_MODE->MODER0 = 0x01;
  pGPIOD_MODE->MODER1 = 0x01;
  pGPIOD_MODE->MODER2 = 0x01;
  pGPIOD_MODE->MODER3 = 0x01;

  // Setting GPIOs PD8-PD11 mode as input (Collumns)
  pGPIOD_MODE->MODER8  = 0x00;
  pGPIOD_MODE->MODER9  = 0x00;
  pGPIOD_MODE->MODER10 = 0x00;
  pGPIOD_MODE->MODER11 = 0x00;

  // Setting GPIOs PD8-PD11 pull-up resistor (Collumns)
  pGPIOD_PUPDR->PUPDR8  = 0x01;
  pGPIOD_PUPDR->PUPDR9  = 0x01;
  pGPIOD_PUPDR->PUPDR10 = 0x01;
  pGPIOD_PUPDR->PUPDR11 = 0x01;
}

static void toggleLED(GPIOx_ODR_t volatile *const pGPIOE_ODR) {
  sleep(1);
  pGPIOE_ODR->ODR4 = 1;
  sleep(1);
  pGPIOE_ODR->ODR4 = 0;
}