#include <stdint.h>
#include <stdbool.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

static void sleep(int secs) {
  #define STEPS_PER_SEC 650000
  unsigned int i,s;
  for (s=0; s < secs; s++) {
    for (i=0; i < STEPS_PER_SEC; i++) {
       // skip CPU cycle or any other statement(s) for making loop 
       // untouched by C compiler code optimizations
       asm("nop");
    }
  }
}

static void toggleLED(void) {
  uint32_t *pRCCAHB1ClockCtrlReg = (uint32_t*) 0x40023830;
  uint32_t *pGEModeReg           = (uint32_t*) 0x40021000;
  uint32_t *pGEOutReg            = (uint32_t*) 0x40021014;

  bool ledSwitch = true;

  *pRCCAHB1ClockCtrlReg |= (1 << 4);
  *pGEModeReg &= (~(3 << 2));
  *pGEModeReg |= (1 << 2);

  while (1) {
	  if (ledSwitch == true) {
      *pGEOutReg |= (1 << 1);
      ledSwitch = false;
    } else {
      *pGEOutReg &= (~(1 << 1));
      ledSwitch = true;
    }
    sleep(2);
  }
}

int main(void)
{  
  toggleLED();

  /* Loop forever */
	for(;;);
}
